import React from 'react';
import './CafeMenu.css'; // Đảm bảo bạn đã tạo file này

function CafeMenu() {
  const drinks = [
    { name: 'Cà phê Espresso', price: '30.000đ', img: 'data:image/jpeg;base64,' },
    { name: 'Cà phê Americano', price: '35.000đ', img: 'data:image/jpeg;base64,' },
    { name: 'Cà phê Latte', price: '40.000đ', img: 'data:image/jpeg;base64,' },
    { name: 'Cà phê Mocha', price: '45.000đ', img: 'data:image/jpeg;base64,' },
  ];

  return (
    <div className="menu">
      <h1>Menu Cà Phê</h1>
      <div className="menu-items">
        {drinks.map((drink, index) => (
          <div className="menu-item" key={index}>
            <img src={drink.img} alt={drink.name} className="menu-image" />
            <div className="menu-details">
              <h2>{drink.name}</h2>
              <p>{drink.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CafeMenu;